import React from "react";
import styled from "@emotion/styled";
import useMoney from "../hooks/useMoney";
import useCryptomoney from "../hooks/useCryptomoney";

const Button = styled.input`
  margin-top: 20px;
  font-weight: bold;
  font-size: 20px;
  padding: 10px;
  background-color: #66a2fe;
  border: none;
  width: 100%;
  border-radius: 10px;
  color: #fff;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #326ac0;
    cursor: pointer;
  }
`;

const Formulario = () => {
  const Currency = [
    { code: "USD", name: "American Dollar" },
    { code: "EUR", name: "Euro" },
    { code: "GBP", name: "Pound Sterling" },
    { code: "QTZ", name: "Quetzal" },
  ];

  // Use useMoney
  const [state, Select, updateState] = useMoney(
    "Choose your currency",
    "",
    Currency
  );

  // Use Criptomoney
  const [criptomoney, SelectCrypto] = useCryptomoney(
    "Choose your criptomoney",
    ""
  );

  return (
    <form>
      <Select />
      <SelectCrypto />
      <Button type="submit" value="Calculate" />
    </form>
  );
};

export default Formulario;
